mapper.select:
select
  id, b_id, name, num, start_time, end_time , status
from brace.brace_msg


        and id = #{id}


        and b_id = #{bId}


        and name = #{name}


        and num = #{num}


        and start_time = #{startTime}


        and end_time = #{endTime}


        and status = #{status}
